import GPTUtil from './gptUtil.js';
import Manager from './manager.js';

export default class GPTAccess {
    constructor() {

        this.gptUtil = new GPTUtil();
        this.manager = new Manager(this.gptUtil);

        //注册事件
        window.GraphApp.eventManager.registerEvent("KeyAAndAltDown", () => {
            
            this.manager.start();
        });
        document.addEventListener("keydown", function (event) {
            
            if (event.code == "KeyA"&&event.altKey) window.GraphApp.eventManager.dispatchKeyAAndAltDown();
            
            
        });
        
    }
  
    
  }
  